import React, { useState, useEffect } from 'react';
import DataTable from 'react-data-table-component';
import Modal from 'react-modal';
import appConfig from '../../config/app.config';
import axios from 'axios';
import Swal from 'sweetalert2';
import Select from "react-select"

Modal.setAppElement('#root')

function PageInventory () {
    const [inventoryList, setInventoryList] = useState([]);
    const [antibodyList, setAntibodyList] = useState([]);
    const [assayTypeList, setAssayTypeList] = useState([]);
    const [barcodeList, setBarcodeList] = useState([]);
    const [cellLineList, setCellLineList] = useState([]);
    const [conditionList, setConditionList] = useState([]);
    const [factoryList, setFactoryList] = useState([]);
    const [labList, setLabList] = useState([]);
    const [sexList, setSexList] = useState([]);
    const [individualList, setIndividualList] = useState([]);
    const [speciesList, setSpeciesList] = useState([]);
    const [strainList, setStrainList] = useState([]);
    const [tissueList, setTissueList] = useState([]);
    const [tissueProcessingList, setTissueProcessingList] = useState([]);
    const [modalShow, setModalShow] = useState(false);
    const [modal, setModal] = useState({});
    const [labID, setlabID] = useState("");
    const [sampleID, setSampleID] = useState("");
    const [assayTypeID, setAssayTypeID] = useState("");
    const [experiment, setExperiment] = useState("");
    const [speciesID, setSpeciesID] = useState("");
    const [strainID, setStrainID] = useState("");
    const [tissueID, setTissueID] = useState("");
    const [cellLineID, setCellLineID] = useState("");
    const [tissueProID, setTissueProID] = useState("");
    const [individualID, setIndividualID] = useState("");
    const [sexID, setSexID] = useState("");
    const [factoryID, setFactoryID] = useState("");
    const [antibodyID, setAntibodyID] = useState("");
    const [conditionID, setConditionID] = useState("");
    const [barcodeID, setBarcodeID] = useState("");
    const [editFlag, setEditFlag] = useState(false);
    const url = `${appConfig.baseURL}/api/`;

    const customStyles = {
        content: {
            top: '40%',
            left: '50%',
            right: 'auto',
            bottom: 'auto',
            transform: 'translate(-50%, -50%)',
            width: '60%'
        }
    };

    const clearInput = () => {
        setlabID("");
        setSampleID("");
        setAssayTypeID("");
        setExperiment("");
        setSpeciesID("");
        setStrainID("");
        setTissueID("");
        setCellLineID("");
        setTissueProID("");
        setIndividualID("");
        setSexID("");
        setFactoryID("");
        setAntibodyID("");
        setConditionID("");
        setBarcodeID("");
    };

    const setInput = (item) => {
        setlabID(item.LabID);
        setSampleID(item.sampleID);
        setAssayTypeID(item.assayTypeID);
        setExperiment(item.experiment);
        setSpeciesID(item.speciesID);
        setStrainID(item.strainID);
        setTissueID(item.tissueID);
        setCellLineID(item.cellLineID);
        setTissueProID(item.tissueProcessingID);
        setIndividualID(item.individualID);
        setSexID(item.sexID);
        setFactoryID(item.factoryID);
        setAntibodyID(item.antibodyID);
        setConditionID(item.conditionID);
        setBarcodeID(item.barcodeID);
    };

    const closeModal = () => {
        setModalShow(false);
        setEditFlag(false);
    };

    const addModal = () => {
        setModalShow(true);
        setModal({ inventory: "", type: "" });
        clearInput();
    };

    const deleteModal = (item) => {
        Swal.fire({
            title: 'Are you sure?',
            text: "You won't be able to revert this!",
            icon: 'warning',
            showCancelButton: true,
            confirmButtonColor: '#3085d6',
            cancelButtonColor: '#d33',
            confirmButtonText: 'Yes, delete it!'
            }).then((result) => {
            if (result.isConfirmed) {
                let deleteURL = url + "/" + item.id;

                axios.delete(deleteURL)
                    .then((resp) => {
                        Swal.fire(
                            'Deleted!',
                            'Your record has been deleted.',
                            'success'
                        );
                        fetchInventory();
                    }).catch((error) => {
                        Swal.fire({
                            icon: 'error',
                            title: 'Oops...',
                            text: 'Something went wrong!'
                        })
                        console.log(`error: ${error.message}`);
                    })
            }
        });
    };

    const save = () => {
        let editURL = url;

        if (editFlag) {
            editURL = editURL + "/" + modal.id;

            axios.put(editURL, { 
                labID: labID, 
                sampleID: sampleID,
                assayTypeID: assayTypeID,
                experiment: experiment,
                speciesID: speciesID,
                strainID: strainID,
                tissueID: tissueID,
                cellLineID: cellLineID,
                tissueProID: tissueProID,
                individualID: individualID,
                sexID: sexID,
                factoryID: factoryID,
                antibodyID: antibodyID,
                conditionID: conditionID,
                barcodeID: barcodeID 
            })
                .then((resp) => {
                    Swal.fire('Updated!', '', 'success')
                    fetchInventory();
                    closeModal();
                }).catch((error) => {
                    Swal.fire({
                            icon: 'error',
                            title: 'Oops...',
                            text: 'Something went wrong!'
                        });
                    console.log(`error: ${error.message}`);
                })
        }
        else{
            axios.post(editURL, { 
                labID: labID, 
                sampleID: sampleID,
                assayTypeID: assayTypeID,
                experiment: experiment,
                speciesID: speciesID,
                strainID: strainID,
                tissueID: tissueID,
                cellLineID: cellLineID,
                tissueProID: tissueProID,
                individualID: individualID,
                sexID: sexID,
                factoryID: factoryID,
                antibodyID: antibodyID,
                conditionID: conditionID,
                barcodeID: barcodeID 
             })
                .then((resp) => {
                    Swal.fire('Saved!', '', 'success');
                    fetchInventory();
                    closeModal();
                }).catch((error) => {
                    Swal.fire({
                            icon: 'error',
                            title: 'Oops...',
                            text: 'Something went wrong!'
                        });
                    console.log(`error: ${error.message}`);
                })
        }
    };

    const columns = [
        {
            name: "Lab",
            selector: 'lab.code'
        },
        {
            name: "Sample ID",
            selector: 'sampleID'
        },
        {
            name: "Assay Type",
            selector: 'assayType.assayType'
        },
        {
            name: "Experiment",
            selector: 'experiment'
        },
        {
            name: "Species",
            selector: 'species.name'
        },
        {
            name: "Strain",
            selector: 'strain.strain'
        },
        {
            name: "Tissue",
            selector: 'tissue.tissue'
        },
        {
            name: "Cell Line",
            selector: 'cellLine.cellLine'
        },
        {
            name: "Tissue Processing",
            selector: 'tissueProcessing.tissueProcessing'
        },
        {
            name: "Individual",
            selector: 'individual.individual'
        },
        {
            name: "Sex",
            selector: 'sex.sex'
        },
        {
            name: "Factory",
            selector: 'factory.factory'
        },
        {
            name: "Antibody",
            selector: 'antibody.antibody'
        },
        {
            name: "Condition",
            selector: 'condition.conditionName'
        },
        {
            name: "Barcode Type",
            selector: 'barcode.type'
        },
        {
            name: "Barcode",
            selector: 'barcode.barcode'
        },
         {
            name: '',
            cell: (c) => {
               return (
                   <div className="form-group">
                        <button onClick={ () => { 
                            setModalShow(true);
                            setEditFlag(true);
                            setModal(c);
                            setInput(c);
                            }}
                            className="btn btn-default">Edit
                        </button>
                        <button onClick={ () => { deleteModal(c) }}
                            className="btn btn-default">Delete
                        </button>
                   </div>
                 )
              }
         }
    ]

    const [filter, setFilter] = useState("");

    const InventoryTable = (props) => {
        const filteredItems = props.inventoryList.filter(item =>
            (
                (item.lab.code && item.lab.code.toLowerCase().includes(props.searchFilter.toLowerCase()))
                || (item.sampleID && item.sampleID.toLowerCase().includes(props.searchFilter.toLowerCase()))
                || (item.assayType.assayType && item.assayType.assayType.toLowerCase().includes(props.searchFilter.toLowerCase()))
                || (item.experiment && item.experiment.toLowerCase().includes(props.searchFilter.toLowerCase()))
                || (item.species.name && item.species.name.toLowerCase().includes(props.searchFilter.toLowerCase()))
                || (item.strain.strain && item.strain.strain.toLowerCase().includes(props.searchFilter.toLowerCase()))
                || (item.tissue.tissue && item.tissue.tissue.toLowerCase().includes(props.searchFilter.toLowerCase()))
                || (item.cellLine.cellLine && item.cellLine.cellLine.toLowerCase().includes(props.searchFilter.toLowerCase()))
                || (item.tissueProcessing.tissueProcessing && item.tissueProcessing.tissueProcessing.toLowerCase().includes(props.searchFilter.toLowerCase()))
                || (item.individual.individual && item.individual.individual.toLowerCase().includes(props.searchFilter.toLowerCase()))
                || (item.sex.sex && item.sex.sex.toLowerCase().includes(props.searchFilter.toLowerCase()))
                || (item.factory.factory && item.factory.factory.toLowerCase().includes(props.searchFilter.toLowerCase()))
                || (item.antibody.antibody && item.antibody.antibody.toLowerCase().includes(props.searchFilter.toLowerCase()))
                || (item.condition.conditionName && item.condition.conditionName.toLowerCase().includes(props.searchFilter.toLowerCase()))
                || (item.barcode.type && item.barcode.type.toLowerCase().includes(props.searchFilter.toLowerCase()))
                || (item.barcode.barcode && item.barcode.barcode.toLowerCase().includes(props.searchFilter.toLowerCase()))
            )    
        )

        return(
            props.inventoryList.length > 0 &&
            <DataTable 
                className="table table-bordered table-primary no-footer"
                noHeader={true}
                columns={props.columns}
                data={ props.searchFilter.trim().length > 0 ? filteredItems : props.inventoryList }
                highlightOnHover
                pagination={true}
                wrap={true}
                paginationRowsPerPageOptions={[10, 20, 40, 50, 100, 200, 1000]}
            />
        )
    };

    const fetchData = (url) => {
        return axios(url);
    };

    const fetchInventory = async () => {
        var res = await fetchData(url + "inventory");
        setInventoryList(res.data);
    };

    const fetchAntibody = async () => {
        var res = await fetchData(url + "antibody");
        let antibodies = res.data;
        let antibodyList = antibodies.map( (item) => {
            return { label: item.antibody, value: item.id }
        });
        setAntibodyList(antibodyList);
    };

    const fetchAssayType = async () => {
        var res = await fetchData(url + "assayType");
        setAssayTypeList(res.data);
    };

    const fetchBarcode = async () => {
        var res = await fetchData(url + "barcode");
        setBarcodeList(res.data);
    };

    const fetchCellLine = async () => {
        var res = await fetchData(url + "cellLine");
        setCellLineList(res.data);
    };

    const fetchCondition = async () => {
        var res = await fetchData(url + "condition");
        setConditionList(res.data);
    };

    const fetchFactory = async () => {
        var res = await fetchData(url + "factory");
        setFactoryList(res.data);
    };

    const fetchIndividual = async () => {
        var res = await fetchData(url + "individual");
        setIndividualList(res.data);
    };

    const fetchLab = async () => {
        var res = await fetchData(url + "lab");
        let labs = res.data;
        let labList = labs.map( (item) => {
            return { label: item.code, value: item.id}
        })
        setLabList(labList);
    };

    const fetchSex = async () => {
        var res = await fetchData(url + "sex");
        setSexList(res.data);
    };

    const fetchSpecies = async () => {
        var res = await fetchData(url + "species");
        setSpeciesList(res.data);
    };

    const fetchStrain = async () => {
        var res = await fetchData(url + "strain");
        setStrainList(res.data);
    };

    const fetchTissue = async () => {
        var res = await fetchData(url + "tissue");
        setTissueList(res.data);
    };

    const fetchTissuePro = async () => {
        var res = await fetchData(url + "tissueProcessing");
        setTissueProcessingList(res.data);
    };

    useEffect(() => {
        fetchInventory();
        fetchAntibody();
        fetchAssayType();
        fetchBarcode();
        fetchCellLine();
        fetchCondition();
        fetchFactory();
        fetchIndividual();
        fetchLab();
        fetchSex();
        fetchSpecies();
        fetchStrain();
        fetchTissue();
        fetchTissuePro();
    }, []);

    return (
        <div className="mainpanel" style={{ marginLeft: '5px' }}>
            <div className="row">
                <div className="pageheader">
                    <h1 className="QC">Inventory Table</h1>
                </div>
            </div>
            <div className="contentpanel">
                <div className="row" style={{margin:'20px 0px 35px 0px'}}>
                    <div className="col-md-12">
                        <div className="panel panel-primary">
                            <div className="panel-heading">
                                <div className="panel-title">
                                    <div><span></span></div>
                                </div>
                            </div>
                            <div className="panel-body" data-ng-init="Show()">
                                <form className="form-inline">
                                    <div className="form-group">
                                        <input type="text" className="form-control" style={{marginLeft:'5px'}} placeholder="Search" onKeyUp={(e) => {setFilter(e.target.value)}}/>
                                        <button type="button" className="btn btn-default" onClick={addModal} >Add</button>
                                    </div>
                                </form>
                            </div>
                        </div>
                    </div>
                </div>
                <div className="row" style={{margin:'10px 0px 35px 0px'}}>
                    <div className="col-md-12">
                        <div className="panel panel-default">
                            <div className="panel-body table-responsive">
                                <InventoryTable 
                                    columns={columns}
                                    inventoryList={inventoryList}
                                    searchFilter={filter}
                                />
                                <Modal
                                    isOpen={modalShow}
                                    style={customStyles}
                                    contentLabel=""       
                                >
                                    <div className="panel">
                                        <button type="button" onClick={closeModal} className="close" data-dismiss="modal" aria-label="Close">
                                            <span aria-hidden="true">&times;</span>
                                        </button>
                                        <div className="panel-heading">
                                            <label>Modal</label>
                                        </div>
                                        <div className="panel-body">
                                            <div className="form-horizontal">
                                                <div className="form-group">
                                                    <label htmlFor="status" className="col-sm-4 control-label">Lab</label>
                                                    <div className="col-lg-8 col-md-8 col-sm-8">
                                                        <Select 
                                                            options={labList}
                                                            onChange={ (e) => {
                                                                setlabID(e.value);
                                                            }} 
                                                        />
                                                    </div>
                                                </div>  
                                                <div className="form-group">
                                                    <label htmlFor="status" className="col-sm-4 control-label">Sample ID</label>
                                                    <div className="col-lg-8 col-md-8 col-sm-8">
                                                        <input type="text" className="form-control" onKeyUp={(e) => { setSampleID(e.target.value); }} placeholder="Sample ID" defaultValue={modal.sampleID} />
                                                    </div>
                                                </div>  
                                                <div className="form-group">
                                                    <label htmlFor="status" className="col-sm-4 control-label">Experiment</label>
                                                    <div className="col-lg-8 col-md-8 col-sm-8">
                                                        <input type="text" className="form-control" onKeyUp={(e) => { setExperiment(e.target.value); }} placeholder="Experiment" defaultValue={modal.experiment} />
                                                    </div>
                                                </div>  
                                            </div>
                                        </div>
                                        <div className="panel-footer">
                                            <div className="row">
                                                <div className="col-lg-3 col-lg-offset-9 col-md-4 col-md-offset-8 col-sm-12">
                                                    <div className="form-group" style={{textAlign: "right"}}>
                                                        <button className="btn btn-primary" onClick={save} >{ editFlag ? "Edit" : "Save"}</button>
                                                        <button type="button" className="btn btn-default" onClick={closeModal} >Cancel</button>
                                                    </div>   
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                </Modal>
                            </div>
                        </div>
                    </div>           
                </div>
            </div>

        </div>
    )
}

export default React.memo(PageInventory)
