import React, { useState, useEffect } from 'react';
import DataTable from 'react-data-table-component';
import Modal from 'react-modal';
import appConfig from '../../config/app.config';
import axios from 'axios';
import Swal from 'sweetalert2';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCog } from '@fortawesome/free-solid-svg-icons'

Modal.setAppElement('#root')

function PageChipSeq() {
    const [showPreLoader, setShowPreLoader] = useState(true);
    const [chipSeqList, setChipSeqList] = useState([]);
    const [modalShow, setModalShow] = useState(false);
    const [file, setFile] = useState([]);
    const url = `${appConfig.baseURL}/api/chipSeq`;

    const customStyles = {
        content: {
            top: '40%',
            left: '50%',
            right: 'auto',
            bottom: 'auto',
            transform: 'translate(-50%, -50%)',
            width: '60%'
        }
    };

    const deleteModal = (item) => {
        Swal.fire({
            title: 'Are you sure?',
            text: "You won't be able to revert this!",
            icon: 'warning',
            showCancelButton: true,
            confirmButtonColor: '#3085d6',
            cancelButtonColor: '#d33',
            confirmButtonText: 'Yes, delete it!'
        }).then((result) => {
            if (result.isConfirmed) {
                setShowPreLoader(true);
                let deleteURL = url + "chipSeq" + "/" + item.id;

                axios.delete(deleteURL)
                    .then((resp) => {
                        setShowPreLoader(false);
                        Swal.fire(
                            'Deleted!',
                            'Your record has been deleted.',
                            'success'
                        );
                        fetchChipSeq();
                    }).catch((error) => {
                        setShowPreLoader(false);
                        Swal.fire({
                            icon: 'error',
                            title: 'Oops...',
                            text: 'Something went wrong!'
                        })
                        console.log(`error: ${error.response.data.message}`);
                    })
            }
        });
    };

    const closeModal = () => {
        setModalShow(false);
        setFile(null);
    };

    const addModal = () => {
        setModalShow(true);
        setFile(null);
    };

    const upload = () => {
        if (file.type !== "text/csv") {
            Swal.fire({
                icon: 'error',
                title: 'Type not acceptable...',
                text: 'Please choose a csv file!'
            });
        }
        else {
            setShowPreLoader(true);

            axios.post(url + "/upload", { file: file })
            .then((resp) => {
                Swal.fire('Uploaded!', '', 'success');
                closeModal();
                setShowPreLoader(false);

            }).catch((error) => {
                Swal.fire({
                        icon: 'error',
                        title: 'Oops...',
                        text: 'Something went wrong!'
                    });
                console.log(`error: ${error.response.data.message}`);
            })
        }
    };

    const columns = [
        {
            name: "Lab",
            selector: 'inventory.lab.code',
            sortable: true
        },
        {
            name: "Sample ID",
            selector: 'inventory.sampleID',
            sortable: true
        },
        {
            name: "Assay Type",
            selector: 'inventory.assayType.assayType',
            sortable: true
        },
        {
            name: "Experiment",
            selector: 'inventory.experiment',
            sortable: true
        },
        {
            name: "Species",
            selector: 'inventory.species.name',
            sortable: true
        },
        {
            name: "Strain",
