import React, { useState, useEffect } from 'react';
import DataTable from 'react-data-table-component';
import Modal from 'react-modal';
import appConfig from '../../config/app.config';
import axios from 'axios';
import Swal from 'sweetalert2';

Modal.setAppElement('#root')

function PageInventory () {
    const [inventoryList, setInventoryList] = useState([]);
    const [modalShow, setModalShow] = useState(false);
    const [modal, setModal] = useState({});
    const [labID, setlabID] = useState("");
    const [sampleID, setSampleID] = useState("");
    const [assayTypeID, setAssayTypeID] = useState("");
    const [experiment, setExperiment] = useState("");
    const [speciesID, setSpeciesID] = useState("");
    const [strainID, setStrainID] = useState("");
    const [tissueID, setTissueID] = useState("");
    const [cellLineID, setCellLineID] = useState("");
    const [tissueProID, setTissueProID] = useState("");
    const [individualID, setIndividualID] = useState("");
    const [sexID, setSexID] = useState("");
    const [factoryID, setFactoryID] = useState("");
    const [antibodyID, setAntibodyID] = useState("");
    const [conditionID, setConditionID] = useState("");
    const [barcodeID, setBarcodeID] = useState("");
    const [editFlag, setEditFlag] = useState(false);
    const url = `${appConfig.baseURL}/api/inventory`;

    const customStyles = {
        content: {
            top: '40%',
            left: '50%',
            right: 'auto',
            bottom: 'auto',
            transform: 'translate(-50%, -50%)',
            width: '60%'
        }
    };

    const clearInput = () => {
        setlabID("");
        setSampleID("");
        setAssayTypeID("");
        setExperiment("");
        setSpeciesID("");
        setStrainID("");
        setTissueID("");
        setCellLineID("");
        setTissueProID("");
        setIndividualID("");
        setSexID("");
        setFactoryID("");
        setAntibodyID("");
        setConditionID("");
        setBarcodeID("");
    };

    const closeModal = () => {
        setModalShow(false);
        setEditFlag(false);
    };

    const addModal = () => {
        setModalShow(true);
        setModal({ inventory: "", type: "" });
        setType("");
        setInventory("");
    };

    const deleteModal = (item) => {
        Swal.fire({
            title: 'Are you sure?',
            text: "You won't be able to revert this!",
            icon: 'warning',
            showCancelButton: true,
            confirmButtonColor: '#3085d6',
            cancelButtonColor: '#d33',
            confirmButtonText: 'Yes, delete it!'
            }).then((result) => {
            if (result.isConfirmed) {
                let deleteURL = url + "/" + item.id;

                axios.delete(deleteURL)
                    .then((resp) => {
                        Swal.fire(
                            'Deleted!',
                            'Your record has been deleted.',
                            'success'
                        );
                        fetchInventory();
                    }).catch((error) => {
                        Swal.fire({
                            icon: 'error',
                            title: 'Oops...',
                            text: 'Something went wrong!'
                        })
                        console.log(`error: ${error.message}`);
                    })
            }
        });
    };

    const save = () => {
        let editURL = url;

        if (editFlag) {
            editURL = editURL + "/" + modal.id;

            axios.put(editURL, { inventory: inventory, type: type })
                .then((resp) => {
                    Swal.fire('Updated!', '', 'success')
                    fetchInventory();
                    closeModal();
                }).catch((error) => {
                    Swal.fire({
                            icon: 'error',
                            title: 'Oops...',
                            text: 'Something went wrong!'
                        });
                    console.log(`error: ${error.message}`);
                })
        }
        else{
            axios.post(editURL, { inventory: inventory, type: type })
                .then((resp) => {
                    Swal.fire('Saved!', '', 'success');
                    fetchInventory();
                    closeModal();
                }).catch((error) => {
                    Swal.fire({
                            icon: 'error',
                            title: 'Oops...',
                            text: 'Something went wrong!'
                        });
                    console.log(`error: ${error.message}`);
                })
        }
    };

    const columns = [
        {
            name: "Lab",
            selector: 'lab'
        },
        {
            name: "Sample ID",
            selector: 'inventory'
        },
        {
            name: "Assay Type",
            selector: 'lab'
        },
        {
            name: "Experiment",
            selector: 'inventory'
        },
        {
            name: "Species",
            selector: 'lab'
        },
        {
            name: "Strain",
            selector: 'inventory'
        },
        {
            name: "Tissue",
            selector: 'lab'
        },
        {
            name: "Cell Line",
            selector: 'inventory'
        },
        {
            name: "Tissue Processing",
            selector: 'inventory'
        },
        {
            name: "Individual",
            selector: 'lab'
        },
        {
            name: "Sex",
            selector: 'inventory'
        },
        {
            name: "Factory",
            selector: 'inventory'
        },
        {
            name: "Antibody",
            selector: 'lab'
        },
        {
            name: "Condition",
            selector: 'inventory'
        },
        {
            name: "Barcode Type",
            selector: 'lab'
        },
        {
            name: "Barcode",
            selector: 'inventory'
        },
         {
            name: '',
            cell: (c) => {
               return (
                   <div className="form-group">
                        <button onClick={ () => { 
                            setModalShow(true);
                            setEditFlag(true);
                            setModal(c);
                            setInventory(c.inventory);
                            setType(c.type);
                            }}
                            className="btn btn-default">Edit
                        </button>
                        <button onClick={ () => { deleteModal(c) }}
                            className="btn btn-default">Delete
                        </button>
                   </div>
                 )
              }
         }
    ]

    const [filter, setFilter] = useState("");

    const InventoryTable = (props) => {
        const filteredItems = props.inventoryList.filter(item =>
            (
                (item.inventory && item.inventory.toLowerCase().includes(props.searchFilter.toLowerCase()))
                || (item.type && item.type.toLowerCase().includes(props.searchFilter.toLowerCase()))
            )    
        )

        return(
            props.inventoryList.length > 0 &&
            <DataTable 
                className="table table-bordered table-primary no-footer"
                noHeader={true}
                columns={props.columns}
                data={ props.searchFilter.trim().length > 0 ? filteredItems : props.inventoryList }
                highlightOnHover
                pagination={true}
                wrap={true}
                paginationRowsPerPageOptions={[10, 20, 40, 50, 100, 200, 1000]}
            />
        )
    };

    const fetchInventory = () => {
        axios(url)
            .then((resp) => {
                let inventoryList = resp.data;
                setInventoryList(inventoryList);
            }).catch((error) => {
                console.log(`error: ${error.message}`);
            })
    };

    useEffect(() => {
        fetchInventory();
    }, []);

    return (
        <div className="mainpanel" style={{ marginLeft: '5px' }}>
            <div className="row">
                <div className="pageheader">
                    <h1 className="QC">Inventory Table</h1>
                </div>
            </div>
            <div className="contentpanel">
                <div className="row" style={{margin:'20px 0px 35px 0px'}}>
                    <div className="col-md-12">
                        <div className="panel panel-primary">
                            <div className="panel-heading">
                                <div className="panel-title">
                                    <div><span></span></div>
                                </div>
                            </div>
                            <div className="panel-body" data-ng-init="Show()">
                                <form className="form-inline">
                                    <div className="form-group">
                                        <input type="text" className="form-control" style={{marginLeft:'5px'}} placeholder="Search" onKeyUp={(e) => {setFilter(e.target.value)}}/>
                                        <button type="button" className="btn btn-default" onClick={addModal} >Add</button>
                                    </div>
                                </form>
                            </div>
                        </div>
                    </div>
                </div>
                <div className="row" style={{margin:'10px 0px 35px 0px'}}>
                    <div className="col-md-12">
                        <div className="panel panel-default">
                            <div className="panel-body table-responsive">
                                <InventoryTable 
                                    columns={columns}
                                    inventoryList={inventoryList}
                                    searchFilter={filter}
                                />
                                <Modal
                                    isOpen={modalShow}
                                    style={customStyles}
                                    contentLabel=""       
                                >
                                    <div className="panel">
                                        <button type="button" onClick={closeModal} className="close" data-dismiss="modal" aria-label="Close">
                                            <span aria-hidden="true">&times;</span>
                                        </button>
                                        <div className="panel-heading">
                                            <label>Modal</label>
                                        </div>
                                        <div className="panel-body">
                                            <div className="form-horizontal">
                                                <div className="form-group">
                                                    <label htmlFor="status" className="col-sm-4 control-label">Type</label>
                                                    <div className="col-lg-8 col-md-8 col-sm-8">
                                                        <input type="text" className="form-control" onKeyUp={(e) => { setType(e.target.value); }} placeholder="Type" defaultValue={modal.type} />
                                                    </div>
                                                </div>  
                                                <div className="form-group">
                                                    <label htmlFor="status" className="col-sm-4 control-label">Inventory</label>
                                                    <div className="col-lg-8 col-md-8 col-sm-8">
                                                        <input type="text" className="form-control" onKeyUp={(e) => { setInventory(e.target.value); }} placeholder="Inventory" defaultValue={modal.inventory} />
                                                    </div>
                                                </div>  
                                            </div>
                                        </div>
                                        <div className="panel-footer">
                                            <div className="row">
                                                <div className="col-lg-3 col-lg-offset-9 col-md-4 col-md-offset-8 col-sm-12">
                                                    <div className="form-group" style={{textAlign: "right"}}>
                                                        <button className="btn btn-primary" onClick={save} >{ editFlag ? "Edit" : "Save"}</button>
                                                        <button type="button" className="btn btn-default" onClick={closeModal} >Cancel</button>
                                                    </div>   
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                </Modal>
                            </div>
                        </div>
                    </div>           
                </div>
            </div>

        </div>
    )
}

export default React.memo(PageInventory)
